public protocol <#coordinator-name#>CoordinatorDelegate: class {
    func <#coordinator-name#>Action(_ coordinator: <#coordinator-name#>Coordinator)
}

public final class <#coordinator-name#>Coordinator: Coordinator {
    private let navigationController: UINavigationController
    public weak var delegate: <#coordinator-name#>CoordinatorDelegate?

    public init(navigationController: UINavigationController) {
        self.navigationController = navigationController
    }

    public func start() {
        showViewController()
    }

    private func showViewController() {
        // Construct viewcontroller
        let viewController = UIViewController()
        // Set delegate self, to handle dismissal
        viewController.delegate = self
        viewController.view.backgroundColor = .blue
        navigationController.viewControllers = [viewController]
    }
}

// MARK: - Delegate callbacks from view controllers

extension <#coordinator-name#>Coordinator: <#coordinator-name#>ViewControllerDelegate {
    func <#coordinator-name#>ViewController() {
        delegate?.<#coordinator-name#>Action(self)
    }
}
